package 기초;

public class 논리연산자 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		boolean b1 = true;
		boolean b2 = false;
		System.out.println("b1 && b2" + (b1 && b2));
		System.out.println("b1 || b2" + (b1 || b2));
		System.out.println("!b1 : " + !b1);
		System.out.println("!b2 : " + !b2);
	}

}
