package 기초;

public class 연습 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int x = 11;
	int y = 8;
		if(x>y) {
			while (x > 0) {
				System.out.printf("%d",x);
				x = x /2;
			}
	
		}else {
			while (y>0) {
				System.out.printf("%d", y);
				y = y/2;
			}
		}
	}

}
